      };

      // icons as Geometry
      var icons = new Dictionary<string, Geometry>();


      // diagram properties
      myDiagram.UndoManager.IsEnabled = true;
      myDiagram.Layout = new TreeLayout();

      // A data binding conversion function. Given an icon name, return a Geometry.
      // This assumes that all icons want to be filled.
      // This caches the Geometry, because the Geometry may be shared by multiple Shapes.
      Geometry GeoFunc(object geonameAsObj, object _) {
        var geoname = geonameAsObj as string;
        Geometry geo = null;
        if (icons.ContainsKey(geoname)) geo = icons[geoname];
        if (geo == null) {
          var geoAsString = iconsStrings[geoname];
          if (geoAsString == null) geoAsString = iconsStrings["heart"];
          geo = Geometry.Parse(geoAsString, true);
          icons.Add(geoname, geo);
          return geo;
        }
        return geo;
      }

      var tooltip =
        Builder.Make<Adornment>("ToolTip").Add(
          new TextBlock {
            Margin = 8,
            Stroke = colors.Gray,
            Font = "Segoe UI, 16px, style=bold"
          }.Bind(
            new Binding("Text", "Geo"))
        );
      var border = tooltip.FindElement("Border") as Shape;
      border.Stroke = colors.Gray;
      border.StrokeWidth = 2;


      // Define a simple template consisting of the icon surrounded by a filled circle
      myDiagram.NodeTemplate =
        new Node(PanelLayoutAuto.Instance) {
          ToolTip = tooltip
        }.Add(
          new Shape {
            Figure = "Circle",
            Fill = "lightcoral",
            StrokeWidth = 0,
            Width = 65,
            Height = 65
          }.Bind(
            new Binding("Fill", "Color")
          ),
          new Shape {
            Margin = 3,
            Fill = colors.White,
            StrokeWidth = 0
          }.Bind(
            new Binding("Geometry", "Geo", GeoFunc)
          )
        );

      // Define a Link template that routes orthogonally, with no arrowhead
      myDiagram.LinkTemplate =
        new Link {
          Routing = LinkRouting.Orthogonal,
          Corner = 5
        }.Add(
          new Shape {
            StrokeWidth = 3.5,
            Stroke = colors.Gray
          }
        ); ; // the link shape

      // model data
      myDiagram.Model = new Model {
        NodeDataSource = new List<NodeData> {
          new NodeData { Key = 1, Geo = "file", Color = colors.Blue },
          new NodeData { Key = 2, Geo = "alarm", Color = colors.Orange },
          new NodeData { Key = 3, Geo = "lab", Color = colors.Blue },
          new NodeData { Key = 4, Geo = "earth", Color = colors.Blue },
          new NodeData { Key = 5, Geo = "heart", Color = colors.Green },
          new NodeData { Key = 6, Geo = "arrow-up-right", Color = colors.Blue },
          new NodeData { Key = 7, Geo = "html5", Color = colors.Orange },
          new NodeData { Key = 8, Geo = "twitter", Color = colors.Orange }
        },
        LinkDataSource = new List<LinkData> {
          new LinkData { From = 1, To = 2 },
          new LinkData { From = 1, To = 3 },
          new LinkData { From = 3, To = 4 },
          new LinkData { From = 4, To = 5 },
          new LinkData { From = 4, To = 6 },
          new LinkData { From = 3, To = 7 },
          new LinkData { From = 3, To = 8 }
        }
      };

      // the second diagram showcases every icon
      myDiagram2.NodeTemplate = myDiagram.NodeTemplate;
      // simple grid layoyut
      myDiagram2.Layout = new GridLayout();

      // convert the icons collection into an array of node data
      var nodeDataSource = new List<NodeData>();
      foreach (var k in iconsStrings) {
        nodeDataSource.Add(new NodeData {
          Geo = k.Key,
          Color = colors.Blue
        });
      }
      myDiagram2.Model = new Model {
        NodeDataSource = nodeDataSource
      };
    }

  }

  // define the model data
  public class Model : GraphLinksModel<NodeData, int, object, LinkData, string, string> { }
  public class NodeData : Model.NodeData {
    public string Geo { get; set; }
    public string Color { get; set; }
  }

  public class LinkData : Model.LinkData { }

}
